import React from "react";
import { StyleSheet, View, Image } from "react-native";


const ShopCard = ({imagem, produto}) => {
    return (
        <View style={estilos.container} >
            <Image source={imagem} style={imagem}/>
        </View>
    )
}

const estilos = StyleSheet.create({
    container: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 1
        },
        shadowOpacity: 0.20,
        shadowRadius: 1.41,
        elevation: 2,
        marginVertical: 20,
        backgroundColor: "#FFF",
        borderRadius: 10
    }

})

export default ShopCard;
