import React from "react";
import { View, Text, StyleSheet, Image, TouchableOpacity } from "react-native";
import usuarioIcne from "../../../assets/usuario_icone.png";
import imagemCelular1 from "../../../assets/celular_1.png";
import oferta1 from "../../../assets/oferta_1.png"


export default function Home() {

    return (
            <View style={estilos.tela}>
                <View style={estilos.cabecalho}>
                    <View >
                        <Text>Bem-Vindo,    </Text>
                        <Text style={estilos.titulo}>Usuário</Text>
                    </View>
                    <View>
                        <Image source={usuarioIcne} style={estilos.imagem}></Image>
                    </View>
                </View>

                <View style={estilos.cartao}>
                    <View>
                        <Text style={estilos.titulo }>Troque e Economize</Text>
                        <Text style={estilos.descricao}>Desfrute de uma ótima economia</Text>
                        <TouchableOpacity style={estilos.botaoSaibaMais}>
                            <Text style={estilos.textoBotaoSaibaMais}>Saiba Mais</Text>
                        </TouchableOpacity>
                    </View>
                    <View>
                        <Image source={imagemCelular1} style={estilos.imagemCartao}></Image>
                    </View>
                </View>

                <View style={estilos.cabecalho}>
                    <Text style={estilos.titulo}>Ofertas Incríveis</Text>
                    <Text style={estilos.linkVerTudo}>Ver Tudo</Text>
                </View>

                <TouchableOpacity style={estilos.cartaoOferta}>
                    <Image source={oferta1} style={estilos.imagemOferta}></Image>
                </TouchableOpacity>

            </View>
    )
}

const estilos = StyleSheet.create({
    tela: {
        margin: 25
    },
    cabecalho:{
        flexDirection:"row",
        justifyContent:"space-between"
    },
    titulo:{
        marginTop:8,
        fontSize:18,
        fontWeight:"bold"
    },
    linkVerTudo:{
        marginTop:8,
        fontSize:18,
        color:"#FF7A00"
    },
    imagem:{
        width: 50,
        height: 50
    },
    cartao:{
        marginVertical:20,
        backgroundColor:"#FFF",
        borderRadius:10,
        flexDirection:"row",
        padding:20,
        shadowColor:"#000",
        shadowOffset:{
            width:0,
            height:1,
        },
        shadowOpacity:0.20,
        shadowRadius:1.41,
        elevation:5
    },
    descricao:{
        marginTop:10,
        width:200
    },
    botaoSaibaMais:{
        backgroundColor: "#FFA959",
        padding: 10,
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#FFA959",
        width: 120,
        marginTop:15,
        opacity:94
    },
    textoBotaoSaibaMais:{
        color:"#FFF",
        textAlign:"center",
        fontWeight:"bold"
    },
    imagemCartao:{
        marginTop:5,
        width: 120,
        height: 120
    },
    cartaoOferta:{
        padding: 15,
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#FFA959"
    },
    imagemOferta: {
        width: 50,
        height: 50,
        marginBottom: 15
    }

    
})
