import React from "react";
import { View, Text, StyleSheet, Image, TouchableOpacity } from "react-native";
import usuarioIcne from "../../../assets/usuario_icone.png";
import imagemCelular1 from "../../../assets/celular_1.png";


export default function Home() {

    return (
            <View style={estilos.tela}>
                <View style={estilos.cabecalho}>
                    <View >
                        <Text>Bem-Vindo,    </Text>
                        <Text style={estilos.titulo}>Usuário</Text>
                    </View>
                    <View>
                        <Image source={usuarioIcne} style={estilos.imagem}></Image>
                    </View>
                </View>
                <View style={estilos.cartao}>
                    <View>
                        <Text style={estilos.titulo }>Troque e Economize</Text>
                        <Text style={estilos.descricao}>Desfrute de uma ótima economia.</Text>
                        <TouchableOpacity style={estilos.botaoSaibaMais}>
                            <Text style={estilos.textoBotaoSaibaMais}>Saiba Mais</Text>
                        </TouchableOpacity>
                    </View>
                    <View>
                        <Image source={imagemCelular1} style={estilos.imagemCartao}></Image>
                    </View>
                </View>
            </View>
    )
}

const estilos = StyleSheet.create({
    tela: {
        margin: 25
    },
    cabecalho:{
        flexDirection:"row",
        justifyContent:"space-between"
    },
    titulo:{
        marginTop:8,
        fontSize:18,
        fontWeight:"bold"
    },
    imagem:{
        width: 50,
        height: 50
    },
    cartao:{
        marginVertical:20,
        backgroundColor:"#FFF",
        borderRadius:10,
        flexDirection:"row",
        padding:20,
        shadowColor:"#000",
        shadowOffset:{
            width:0,
            height:1,
        },
        shadowOpacity:0.20,
        shadowRadius:1.41,
        elevation:5
    },
    descricao:{

    },
    botaoSaibaMais:{
        backgroundColor: "#FF7A00",
        padding: 10,
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#FF7A00",
        width: 170

    },
    textoBotaoSaibaMais:{

    },
    imagemCartao:{
        width: 50,
        height: 50
    }

    
})
